const Stars = ({ rating }) => {
  const fullStars = Math.floor(rating);
  const halfStar = rating - fullStars >= 0.5;
  const emptyStars = 5 - fullStars - (halfStar ? 1 : 0);
  return (
    <div style={{ color: "#f5c518", fontSize: "1.3rem" }}>
      {"★".repeat(fullStars)}
      {halfStar && "⯪"}
      {"☆".repeat(emptyStars)}
    </div>
  );
};

const styles = {
  container: {
    fontFamily: "'Poppins', sans-serif",
    width: "80vw",
    margin: "40px auto",
    padding: "0 10px",
    boxSizing: "border-box",
  },
  header: {
    fontSize: 38,
    fontWeight: "700",
    textAlign: "center",
    marginBottom: 45,
    color: "#222",
    width: "100%",
  },
  grid: {
    display: "grid",
    gridTemplateColumns: "repeat(4, 18vw)",
    gap: "25px",
    justifyContent: "center",
    maxWidth: "100%",
    margin: "0 auto",
  },
  card: {
    perspective: 1200,
  },
  cardInner: {
    position: "relative",
    width: "100%",
    paddingTop: "150%",
    transformStyle: "preserve-3d",
    transition: "transform 0.6s",
    cursor: "pointer",
    borderRadius: 20,
    boxShadow: "0 8px 22px rgba(0,0,0,0.15)",
    backgroundColor: "#fff",
  },
  cardInnerFlipped: {
    transform: "rotateY(180deg)",
  },
  cardFace: {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    borderRadius: 20,
    backfaceVisibility: "hidden",
    overflow: "hidden",
  },
  cardFront: {
    backgroundColor: "#fff",
  },
  cardBack: {
    backgroundColor: "#f9f9f9",
    color: "#333",
    transform: "rotateY(180deg)",
    padding: 22,
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  image: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
  },
  title: {
    fontWeight: "700",
    fontSize: 21,
    padding: "12px 15px",
    textAlign: "center",
    backgroundColor: "#222",
    color: "#fff",
  },
  detailText: {
    fontSize: 14.5,
    lineHeight: 1.5,
    marginBottom: 18,
  },
  genres: {
    fontSize: 14,
    fontWeight: "600",
    marginBottom: 14,
    color: "#555",
  },
  year: {
    fontSize: 14,
    fontWeight: "600",
    marginBottom: 14,
    color: "#888",
  },
  button: {
    backgroundColor: "#ff3b3f",
    color: "white",
    border: "none",
    borderRadius: 10,
    padding: "12px 16px",
    fontWeight: "600",
    fontSize: 15,
    cursor: "pointer",
    textDecoration: "none",
    textAlign: "center",
    userSelect: "none",
    transition: "background-color 0.3s",
  },
  buttonHover: {
    backgroundColor: "#e32b2f",
  },
};

export default function TopMovies() {
  const [flipped, setFlipped] = useState(null);
  const [buttonHoverIndex, setButtonHoverIndex] = useState(null);

  return (
    <>
      <style>{`
        @media (max-width: 950px) {
          .movie-grid {
            grid-template-columns: repeat(2, 40vw) !important;
          }
        }
        @media (max-width: 550px) {
          .movie-grid {
            grid-template-columns: 90vw !important;
          }
        }
      `}</style>
      <div style={styles.container}>
        <h2 style={styles.header}>Top Movies 🎬</h2>
        <div className="movie-grid" style={styles.grid}>
          {movies.map((movie, index) => (
            <div
              key={movie.title}
              style={styles.card}
              onClick={() => setFlipped(flipped === index ? null : index)}
            >
              <div
                style={{
                  ...styles.cardInner,
                  ...(flipped === index ? styles.cardInnerFlipped : {}),
                }}
              >
                {/* Front */}
                <div style={{ ...styles.cardFace, ...styles.cardFront }}>
                  <img
                    src={movie.image}
                    alt={movie.title}
                    style={styles.image}
                  />
                  <div style={styles.title}>{movie.title}</div>
                </div>

                {/* Back */}
                <div style={{ ...styles.cardFace, ...styles.cardBack }}>
                  <div>
                    <div style={styles.genres}>{movie.genres.join(", ")}</div>
                    <div style={styles.year}>Released: {movie.year}</div>
                    <div style={styles.detailText}>{movie.detail}</div>
                    <Stars rating={movie.rating} />
                  </div>
                  <a
                    href={movie.trailer}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                      ...styles.button,
                      ...(buttonHoverIndex === index ? styles.buttonHover : {}),
                    }}
                    onMouseEnter={() => setButtonHoverIndex(index)}
                    onMouseLeave={() => setButtonHoverIndex(null)}
                    onClick={(e) => e.stopPropagation()}
                  >
                    ▶ Watch Trailer
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
